DROP TABLE IF EXISTS banned_ip;
CREATE TABLE banned_ip(
   id_ip      INT GENERATED ALWAYS AS IDENTITY
  ,ip     VARCHAR(30) NOT NULL
  ,created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
	);


DROP TABLE IF EXISTS currency_by_country;
CREATE TABLE currency_by_country(
   id_cur      INT GENERATED ALWAYS AS IDENTITY
  ,country     VARCHAR(44) NOT NULL
  ,country_code VARCHAR(4) NOT NULL
  ,currency    VARCHAR(39) NOT NULL
  ,code        VARCHAR(3) NOT NULL
	);
